
public class Ifelse {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		int var1=10;
		int var2=20;
		int var3=30;
		
		if(var1>var2 & var1>var3)
		{
			System.out.println("var1 variable is greater than var2 and var3 variable");
			
		}
		if(var2>var3)
		{
			System.out.println("var2 variable is greater than var3 and var1 variable");
		}
		else
		{
			System.out.println("var3 variable is greater than var1 and var2");
		}
	}

}
